const hexString = '';

// Function to convert hex string to a UTF-8 string
function hexToUtf8(hex) {
    const bytes = Buffer.from(hex, 'hex');
    return bytes.toString('utf8');
}

const decodedString = hexToUtf8(hexString);
console.log(decodedString);


